import React, { useState } from 'react';

const Settings = () => {
  const [settings, setSettings] = useState({
    darkMode: false,
    notifications: true,
  });
  const [saved, setSaved] = useState(false);

  const handleChange = (e) => {
    const { name, type, checked } = e.target;
    setSettings({ ...settings, [name]: type === 'checkbox' ? checked : e.target.value });
    setSaved(false);
  };

  const handleSave = (e) => {
    e.preventDefault();
    setSaved(true);
    // Save logic here (future: send to backend)
  };

  return (
    <div className="max-w-xl mx-auto bg-white rounded-xl shadow-lg p-6 mt-6">
      <h1 className="text-2xl font-bold mb-4">Settings</h1>
      <form onSubmit={handleSave} className="space-y-6">
        <div className="flex items-center justify-between">
          <label className="text-gray-700 font-medium">Dark Mode (coming soon)</label>
          <input
            type="checkbox"
            name="darkMode"
            checked={settings.darkMode}
            onChange={handleChange}
            className="w-5 h-5 text-blue-600 border-gray-300 rounded"
            disabled
          />
        </div>
        <div className="flex items-center justify-between">
          <label className="text-gray-700 font-medium">Enable Notifications (coming soon)</label>
          <input
            type="checkbox"
            name="notifications"
            checked={settings.notifications}
            onChange={handleChange}
            className="w-5 h-5 text-blue-600 border-gray-300 rounded"
            disabled
          />
        </div>
        <button
          type="submit"
          className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 font-semibold"
        >
          Save
        </button>
        {saved && <div className="text-green-600 font-medium mt-2">Settings saved!</div>}
      </form>
    </div>
  );
};

export default Settings;
